const API_URL = 'http://localhost/api';

export const fetchUsers = async () => {
    const response = await fetch(`${API_URL}/users/person`);
    if (!response.ok) {
        throw new Error('Failed to fetch users');
    }
    return response.json();
};

export const fetchDevices = async () => {
    const response = await fetch(`${API_URL}/devices/device`);
    if (!response.ok) {
        throw new Error('Failed to fetch devices');
    }
    return response.json();
};

export const assignDeviceToUser = async (userId, deviceId) => {
    const response = await fetch(`${API_URL}/devices/user-device`, {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
        },
        body: JSON.stringify({ userId, deviceId }),
    });
    if (!response.ok) {
        throw new Error('Failed to assign device');
    }
    return response.json();
};
