const API_URL = 'http://localhost/api';

export const fetchUsers = async () => {
    const response = await fetch(`${API_URL}/users/people`);
    if (!response.ok) throw new Error('Failed to fetch users');
    return response.json();
};

export const createUser = async (userData) => {
    const response = await fetch(`${API_URL}/users/people`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(userData),
    });
    if (!response.ok) throw new Error('Failed to create user');
    return response;
};

export const updateUser = async (id, userData) => {
    const response = await fetch(`${API_URL}/users/people/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(userData),
    });
    if (!response.ok) throw new Error('Failed to update user');
    return response;
};

export const deleteUser = async (id) => {
    const response = await fetch(`${API_URL}/users/people/${id}`, {
        method: 'DELETE',
    });
    if (!response.ok) throw new Error('Failed to delete user');
    return response;
};

export const deleteAllUsers = async () => {
    const response = await fetch(`${API_URL}/users/people`, {
        method: 'DELETE',
    });
    if (!response.ok) throw new Error('Failed to delete all users');
    return response;
};


export const fetchDevices = async () => {
    const response = await fetch(`${API_URL}/devices/devices`);
    if (!response.ok) throw new Error('Failed to fetch devices');
    return response.json();
};

export const createDevice = async (deviceData) => {
    const response = await fetch(`${API_URL}/devices/devices`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(deviceData),
    });
    if (!response.ok) throw new Error('Failed to create device');
    return response;
};

export const updateDevice = async (id, deviceData) => {
    const response = await fetch(`${API_URL}/devices/devices/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(deviceData),
    });
    if (!response.ok) throw new Error('Failed to update device');
    return response;
};

export const updateDeviceName = async (id, name) => {
    const response = await fetch(`${API_URL}/devices/devices/name/${id}`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ name }),
    });
    if (!response.ok) throw new Error('Failed to update device name');
    return response;
};

export const updateDeviceConsumption = async (id, consumption) => {
    const response = await fetch(`${API_URL}/devices/devices/consumption/${id}`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ consumption }),
    });
    if (!response.ok) throw new Error('Failed to update device consumption');
    return response;
};

export const deleteAllDevices = async () => {
    const response = await fetch(`${API_URL}/devices/devices`, {
        method: 'DELETE',
    });
    if (!response.ok) throw new Error('Failed to delete all devices');
    return response;
};


export const assignDeviceToUser = async (userID, deviceID) => {
    const response = await fetch(`${API_URL}/devices/devices/assign`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ userID, deviceID }),
    });
    if (!response.ok) throw new Error('Failed to assign device');
    return response;
};

export const fetchAssignments = async () => {
    const response = await fetch(`${API_URL}/devices/devices/assign`);
    if (!response.ok) throw new Error('Failed to fetch assignments');
    return response.json();
};

export const deleteAllAssignments = async () => {
    const response = await fetch(`${API_URL}/devices/devices/assign`, {
        method: 'DELETE',
    });
    if (!response.ok) throw new Error('Failed to delete all assignments');
    return response;
};
