import React, { useState, useEffect } from 'react';
import { fetchUsers, fetchDevices, assignDeviceToUser } from '../api';

function AssignDevicePage() {
    const [users, setUsers] = useState([]);
    const [devices, setDevices] = useState([]);
    const [selectedUser, setSelectedUser] = useState('');
    const [selectedDevice, setSelectedDevice] = useState('');
    const [message, setMessage] = useState('');
    const [error, setError] = useState(null);

    useEffect(() => {
        const loadData = async () => {
            try {
                const usersData = await fetchUsers();
                const devicesData = await fetchDevices();
                setUsers(usersData);
                setDevices(devicesData);
                if (usersData.length > 0) setSelectedUser(usersData[0].id);
                if (devicesData.length > 0) setSelectedDevice(devicesData[0].id);
            } catch (err) {
                setError(err.message);
            }
        };
        loadData();
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await assignDeviceToUser(selectedUser, selectedDevice);
            setMessage(`Successfully assigned device ${selectedDevice} to user ${selectedUser}`);
        } catch (err) {
            setError(err.message);
        }
    };
    
    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <div>
            <h2>Assign Device to User</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>User:</label>
                    <select value={selectedUser} onChange={e => setSelectedUser(e.target.value)}>
                        {users.map(user => (
                            <option key={user.id} value={user.id}>{user.name}</option>
                        ))}
                    </select>
                </div>
                <div>
                    <label>Device:</label>
                    <select value={selectedDevice} onChange={e => setSelectedDevice(e.target.value)}>
                        {devices.map(device => (
                            <option key={device.id} value={device.id}>{device.description}</option>
                        ))}
                    </select>
                </div>
                <button type="submit">Assign Device</button>
            </form>
            {message && <p>{message}</p>}
        </div>
    );
}

export default AssignDevicePage;
